mapper.select:
select a.*,s.*,t.applytype_name,p1.name as passiveName,d.department_name from t_salaryadjust as s, t_apply as a, t_applytype as t, t_department as d, t_personalinfo as p1 where s.apply_id=a.id and a.apply_typeid=t.applytype_id and a.passive_id=p1.job_id and p1.department_id=d.department_id and a.write_id=#{jobId}
and salaryadjust_type like '${salaryadjust_type}%'
and state like '${state}%'
and p1.department_id like '${department_id}%'
LIMIT #{before},#{after}

select count(*) from t_salaryadjust as s,t_apply as a 
    where s.apply_id=a.id 
          and s.salaryadjust_money=#{salaryadjustMoneny}
          and s.salaryadjust_type=#{salaryadjustType}
          and a.write_id=#{apply.writeId} and a.passive_id=#{apply.passiveId}
          and a.submit_date=#{apply.submitDate} and a.reason=#{apply.reason}

select a.*,s.*,t.applytype_name,p1.name as passiveName, p2.name as writeName ,d.department_name from t_salaryadjust as s, t_apply as a, t_applytype as t, t_department as d,t_occupation as o, t_personalinfo as p1,t_personalinfo as p2 where s.apply_id=a.id and a.apply_typeid=t.applytype_id and a.passive_id=p1.job_id and a.write_id=p2.job_id and p1.department_id=d.department_id and p1.occupation_id=o.occupation_id and p2.occupation_id=#{writeOccupationId}
and salaryadjust_type like '${salaryadjust_type}%'
and state like '${state}%'
and p1.department_id like '${department_id}%'
LIMIT #{before},#{after}

select a.*,s.*,t.applytype_name,p2.name as writeName from t_salaryadjust as s, t_apply as a, t_applytype as t,t_personalinfo as p2 where s.apply_id=a.id and a.apply_typeid=t.applytype_id and a.write_id=p2.job_id and a.passive_id=#{jobId}
and state like '${state}%'
LIMIT #{before},#{after}
